let rawData = msg.payload;

// 1. Extrai o array de dados (resolve array duplo)
let data;
if (rawData.ecommerce_sales) {
    // Se vier com estrutura {"ecommerce_sales": [[...]]}
    data = rawData.ecommerce_sales[0];
} else if (Array.isArray(rawData)) {
    // Se vier como array direto
    data = rawData;
} else {
    msg.payload = [{
        "series": ["Erro"],
        "data": [[0]],
        "labels": ["Formato invÃ¡lido"]
    }];
    node.error("âŒ Formato de dados nÃ£o reconhecido!");
    return msg;
}

// 2. Verifica se hÃ¡ dados
if (!Array.isArray(data) || data.length === 0) {
    msg.payload = [{
        "series": ["Aviso"],
        "data": [[0]],
        "labels": ["Sem dados"]
    }];
    node.warn("âš ï¸ Array de dados vazio");
    return msg;
}

// 3. PROCESSA: Conta vendas por produto (TOP 10)
let produtosCount = {};

data.forEach(row => {
    let produto = row.product_name || "Desconhecido";
    produtosCount[produto] = (produtosCount[produto] || 0) + 1;
});

// 4. Ordena e pega os TOP 10 produtos mais vendidos
let sorted = Object.entries(produtosCount)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 10);

// 5. Prepara dados para o grÃ¡fico de barras
msg.payload = [{
    "series": ["Vendas"],
    "data": [sorted.map(item => item[1])],
    "labels": sorted.map(item => item[0])
}];

// 6. Log para debug
node.warn("âœ… Processados " + data.length + " registos");
node.warn("ðŸ“Š TOP 10 produtos: " + sorted.map(p => p[0] + ":" + p[1]).join(", "));

return msg;

let rawData = msg.payload;
